import { useState } from "react";

export default function Account() {
  const [user, setUser] = useState({
    pseudo: "Charly",
    email: "charly@example.com",
    prenom: "Charly",
    nom: "Dery",
    telephone: "+1 819-209-XXXX",
    pays: "Canada",
    ville: "Gatineau",
    adresse: "123 rue des joueurs",
    naissance: "2003-06-15"
  });

  const [editMode, setEditMode] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setEditMode(false);
    alert("✅ Informations mises à jour !");
  };

  return (
    <div className="max-w-2xl mx-auto mt-10 bg-white p-6 shadow rounded">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-blue-700">Mon Compte</h1>
        <button
          onClick={() => setEditMode(!editMode)}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
        >
          {editMode ? "Annuler" : "Modifier"}
        </button>
      </div>
      <div className="mb-6 text-center">
  <h2 className="text-xl font-semibold mb-2">Photo de profil</h2>
  <img
    src={user.avatar}
    alt="Avatar"
    className="mx-auto mb-2 w-24 h-24 rounded-full object-cover border-2 border-blue-600"
  />
  {editMode && (
    <div className="flex justify-center gap-4 mt-4 flex-wrap">
      {["avatar1.png", "avatar2.png", "avatar3.png"].map((img) => (
        <img
          key={img}
          src={`/avatars/${img}`}
          alt={img}
          onClick={() => setUser({ ...user, avatar: `/avatars/${img}` })}
          className={`w-16 h-16 rounded-full border-2 cursor-pointer ${
            user.avatar === `/avatars/${img}` ? "border-blue-600" : "border-gray-300"
          }`}
        />
      ))}
    </div>
  )}
</div>

      <form onSubmit={handleSubmit} className="grid grid-cols-1 sm:grid-cols-2 gap-4">
        {Object.entries(user).map(([key, value]) => (
          <div key={key}>
            <label className="font-semibold capitalize">{key}</label>
            {editMode ? (
              <input
                name={key}
                value={value}
                onChange={handleChange}
                className="w-full p-2 border rounded"
              />
            ) : (
              <p className="text-gray-700">{value}</p>
            )}
          </div>
        ))}

        {editMode && (
          <div className="sm:col-span-2 flex justify-end mt-4">
            <button
              type="submit"
              className="bg-green-600 hover:bg-green-700 text-white px-6 py-2 rounded"
            >
              Enregistrer
            </button>
          </div>
        )}
      </form>
    </div>
  );
}
