import React, { useState } from "react";

const tournaments = [
  {
    id: 1,
    name: "Tournoi Solo #18",
    date: "25 avril 2025",
    players: 74,
    maxPlayers: 100,
    prize: "700 $",
    entryFee: 10,
  },
  {
    id: 2,
    name: "Tournoi Duo #05",
    date: "30 avril 2025",
    players: 56,
    maxPlayers: 100,
    prize: "1000 $",
    entryFee: 15,
  },
  {
    id: 3,
    name: "Tournoi Team #02",
    date: "4 mai 2025",
    players: 20,
    maxPlayers: 20,
    prize: "10 000 $",
    entryFee: 25,
  },
];

export default function Competitions() {
  const [balance, setBalance] = useState(50); // Solde du joueur
  const [selectedTournament, setSelectedTournament] = useState(null);
  const [message, setMessage] = useState("");

  const handleJoinClick = (tournament) => {
    if (tournament.players >= tournament.maxPlayers) {
      setMessage("❌ Ce tournoi est complet.");
      return;
    }
    if (balance < tournament.entryFee) {
      setMessage("❌ Solde insuffisant pour ce tournoi.");
      return;
    }
    setSelectedTournament(tournament);
    setMessage("");
  };

  const closeModal = () => {
    setSelectedTournament(null);
  };

  const confirmJoin = () => {
    setBalance((prev) => prev - selectedTournament.entryFee);
    alert(`✅ Inscription confirmée à ${selectedTournament.name}`);
    closeModal();
  };

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6 text-center text-blue-700">
        Compétitions en cours
      </h1>

      <h2 className="text-right text-lg mb-4">
        💰 Solde : <span className="font-bold">{balance.toFixed(2)} $</span>
      </h2>

      {message && (
        <div className="mb-4 p-3 bg-red-100 border border-red-400 text-red-600 rounded">
          {message}
        </div>
      )}

      <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {tournaments.map((t) => (
          <div
            key={t.id}
            className="bg-white shadow-md rounded-lg p-4 border hover:shadow-lg transition"
          >
            <h2 className="text-xl font-bold text-gray-800">{t.name}</h2>
            <p className="text-sm text-gray-500 mb-1">📅 {t.date}</p>
            <p className="text-sm text-gray-600">
              🧑‍💻 File Solo (équipes générées automatiquement)
            </p>
            <p className="text-sm text-gray-600">
              👥 {t.players} / {t.maxPlayers} joueurs
            </p>
            <p className="text-sm text-green-600 mb-2">💰 Cashprize : {t.prize}</p>
            <p className="text-sm mb-4">
              🎟️ Entrée : <strong>{t.entryFee} $</strong>
            </p>
            <button
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 w-full"
              onClick={() => handleJoinClick(t)}
            >
              Participer
            </button>
          </div>
        ))}
      </div>

      {selectedTournament && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-xl shadow-lg w-full max-w-md">
            <h2 className="text-xl font-bold mb-4 text-center">
              Participer à {selectedTournament.name} ?
            </h2>
            <p className="mb-2 text-center">
              🧾 Coût d’inscription :{" "}
              <strong>{selectedTournament.entryFee} $</strong>
            </p>
            <p className="mb-4 text-center">
              Ton solde sera débité et tu seras ajouté à la file solo.
            </p>
            <div className="flex justify-between">
              <button
                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                onClick={confirmJoin}
              >
                Confirmer
              </button>
              <button
                className="bg-gray-400 text-white px-4 py-2 rounded hover:bg-gray-500"
                onClick={closeModal}
              >
                Annuler
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
