import React, { useState } from "react";

const avatarList = [
  "jett.png",
  "raze.png",
  "reyna.png",
  "fade.png",
  // ajoute ici tous les noms des fichiers d’agents que tu as dans public/avatars
];

export default function Account() {
  const [formData, setFormData] = useState({
    prenom: "",
    nom: "",
    email: "",
    phone: "",
    pays: "",
    ville: "",
    adresse: "",
    naissance: "",
    avatar: avatarList[0], // par défaut Jett
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleAvatarSelect = (avatar) => {
    setFormData(prev => ({ ...prev, avatar }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Infos sauvegardées :", formData);
    // ici tu pourras connecter à une base de données plus tard
  };

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Mon compte</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <div className="grid grid-cols-2 gap-4">
          <input name="prenom" value={formData.prenom} onChange={handleChange} placeholder="Prénom" className="p-2 border rounded" />
          <input name="nom" value={formData.nom} onChange={handleChange} placeholder="Nom" className="p-2 border rounded" />
          <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" type="email" className="p-2 border rounded" />
          <input name="phone" value={formData.phone} onChange={handleChange} placeholder="Téléphone" className="p-2 border rounded" />
          <input name="pays" value={formData.pays} onChange={handleChange} placeholder="Pays" className="p-2 border rounded" />
          <input name="ville" value={formData.ville} onChange={handleChange} placeholder="Ville" className="p-2 border rounded" />
          <input name="adresse" value={formData.adresse} onChange={handleChange} placeholder="Adresse" className="p-2 border rounded" />
          <input name="naissance" value={formData.naissance} onChange={handleChange} placeholder="Date de naissance" type="date" className="p-2 border rounded" />
        </div>

        <div>
          <h3 className="font-semibold mb-2">Choisis ta photo de profil</h3>
          <div className="flex space-x-4 overflow-x-auto">
            {avatarList.map((avatar) => (
              <img
                key={avatar}
                src={`/avatars/${avatar}`}
                alt={avatar}
                className={`w-20 h-20 border-4 rounded-full cursor-pointer ${
                  formData.avatar === avatar ? "border-blue-500" : "border-transparent"
                }`}
                onClick={() => handleAvatarSelect(avatar)}
              />
            ))}
          </div>
        </div>

        <button type="submit" className="mt-4 px-6 py-2 bg-blue-600 text-white rounded">Sauvegarder</button>
      </form>
    </div>
  );
}
