import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useState } from "react";

// Auth
import { AuthProvider, useAuth } from "./auth/AuthContext";
import RequireAuth from "./auth/RequireAuth";

// Pages
import Home from "./pages/Home";
import Account from "./pages/Account";

// Header local
function Header({ toggleSidebar }) {
  const { user, logout } = useAuth();

  return (
    <header className="bg-red-600 text-white px-6 py-3 flex justify-between items-center">
      <h1 className="text-xl font-bold">CDesport</h1>
      <div className="flex items-center space-x-4">
        {user ? (
          <>
            <span className="text-sm">ðŸ‘¤ {user.email}</span>
            <button
              onClick={logout}
              className="bg-white text-red-600 px-3 py-1 rounded font-semibold"
            >
              DÃ©connexion
            </button>
          </>
        ) : (
          <Link to="/login" className="bg-white text-red-600 px-3 py-1 rounded font-semibold">
            Connexion
          </Link>
        )}
      </div>
    </header>
  );
}

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <AuthProvider>
      <Router>
        <Header toggleSidebar={() => setSidebarOpen(!sidebarOpen)} />
        <main className="p-6">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route
              path="/account"
              element={
                <RequireAuth>
                  <Account />
                </RequireAuth>
              }
            />
          </Routes>
        </main>
      </Router>
    </AuthProvider>
  );
}

export default App;
