// src/components/Header.jsx
import React from "react";
import { Link } from "react-router-dom";

const Header = ({ toggleSidebar }) => {
  const isLoggedIn = !!localStorage.getItem("token"); // simple check

  return (
    <header className="flex justify-between items-center bg-red-600 text-white p-4">
      <button onClick={toggleSidebar} className="text-xl font-bold">
        ☰ Menu
      </button>

      <h1 className="text-2xl font-bold">CDesport</h1>

      {!isLoggedIn ? (
        <Link
          to="/auth"
          className="bg-white text-red-600 px-4 py-2 rounded hover:bg-gray-200 font-semibold"
        >
          Connexion / Inscription
        </Link>
      ) : (
        <span className="text-sm">Connecté</span> // à remplacer par pseudo plus tard
      )}
    </header>
  );
};

export default Header;
