import React, { useState } from "react";

export default function Account() {
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({
    nom: "Charly Dery",
    email: "charly@example.com",
    phone: "+1 819-555-1234",
    country: "Canada",
    city: "Gatineau",
    birthdate: "2004-09-12",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    // ici tu pourrais envoyer vers une API
    setEditing(false);
  };

  const handleCancel = () => {
    setEditing(false);
    // Remettre les valeurs initiales si besoin (dans un vrai cas on garde une copie)
  };

  return (
    <div className="max-w-4xl mx-auto mt-10 p-6 bg-white rounded-xl shadow-md">
      <h1 className="text-3xl font-bold mb-6 text-center text-blue-700">
        Mon Compte
      </h1>

      {/* Infos personnelles */}
      <div className="mb-8">
        <div className="flex justify-between items-center">
          <h2 className="text-xl font-semibold mb-2">Informations personnelles</h2>
          {!editing ? (
            <button
              className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700"
              onClick={() => setEditing(true)}
            >
              Modifier
            </button>
          ) : (
            <div className="space-x-2">
              <button
                className="bg-green-600 text-white px-3 py-1 rounded hover:bg-green-700"
                onClick={handleSave}
              >
                Enregistrer
              </button>
              <button
                className="bg-gray-400 text-white px-3 py-1 rounded hover:bg-gray-500"
                onClick={handleCancel}
              >
                Annuler
              </button>
            </div>
          )}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-2">
          <div>
            <label><strong>Nom :</strong></label><br />
            {editing ? (
              <input
                name="nom"
                value={formData.nom}
                onChange={handleChange}
                className="w-full border p-1 rounded"
              />
            ) : (
              formData.nom
            )}
          </div>

          <div>
            <label><strong>Email :</strong></label><br />
            {editing ? (
              <input
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="w-full border p-1 rounded"
              />
            ) : (
              formData.email
            )}
          </div>

          <div>
            <label><strong>T√©l√©phone :</strong></label><br />
            {editing ? (
              <input
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                className="w-full border p-1 rounded"
              />
            ) : (
              formData.phone
            )}
          </div>

          <div>
            <label><strong>Pays :</strong></label><br />
            {editing ? (
              <input
                name="country"
                value={formData.country}
                onChange={handleChange}
                className="w-full border p-1 rounded"
              />
            ) : (
              formData.country
            )}
          </div>

          <div>
            <label><strong>Ville :</strong></label><br />
            {editing ? (
              <input
                name="city"
                value={formData.city}
                onChange={handleChange}
                className="w-full border p-1 rounded"
              />
            ) : (
              formData.city
            )}
          </div>

          <div>
            <label><strong>Date de naissance :</strong></label><br />
            {editing ? (
              <input
                name="birthdate"
                type="date"
                value={formData.birthdate}
                onChange={handleChange}
                className="w-full border p-1 rounded"
              />
            ) : (
              formData.birthdate
            )}
          </div>
        </div>
      </div>

      {/* R√©compenses */}
      <div className="mb-8">
        <h2 className="text-xl font-semibold mb-2">R√©compenses</h2>
        <ul className="list-disc list-inside">
          <li>üèÜ 1er place - Tournoi Solo #17 - 100 $</li>
          <li>ü•à 2e place - Tournoi Duo #04 - 60 $</li>
        </ul>
      </div>

      {/* Historique */}
      <div>
        <h2 className="text-xl font-semibold mb-2">Participations r√©centes</h2>
        <table className="w-full table-auto border">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-2 border">Tournoi</th>
              <th className="p-2 border">R√©sultat</th>
              <th className="p-2 border">Date</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td className="p-2 border">Tournoi Solo #17</td>
              <td className="p-2 border">1er place</td>
              <td className="p-2 border">21 avril 2025</td>
            </tr>
            <tr>
              <td className="p-2 border">Tournoi Duo #04</td>
              <td className="p-2 border">2e place</td>
              <td className="p-2 border">7 avril 2025</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
}
