import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useState } from "react";

// Auth
import { AuthProvider, useAuth } from "./auth/AuthContext";
import RequireAuth from "./auth/RequireAuth";

// Pages
import Login from "./auth/Login";
import Account from "./pages/Account";

function Header() {
  const { user, logout } = useAuth();
  return (
    <header className="bg-red-600 text-white px-6 py-3 flex justify-between">
      <h1 className="text-xl font-bold">CDesport</h1>
      {user ? (
        <div className="flex gap-4 items-center">
          <span>{user.email}</span>
          <button onClick={logout} className="bg-white text-red-600 px-3 py-1 rounded">
            D√©connexion
          </button>
        </div>
      ) : (
        <Link to="/login" className="bg-white text-red-600 px-3 py-1 rounded">
          Connexion
        </Link>
      )}
    </header>
  );
}

function App() {
  return (
    <AuthProvider>
      <Router>
        <Header />
        <main className="p-6">
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route
              path="/account"
              element={
                <RequireAuth>
                  <Account />
                </RequireAuth>
              }
            />
            <Route path="/" element={<div className="text-center">üè† Page d'accueil</div>} />
          </Routes>
        </main>
      </Router>
    </AuthProvider>
  );
}

export default App;
