import { useState } from "react";

export default function LinkValorant() {
  const [riotId, setRiotId] = useState("");
  const [linked, setLinked] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simule un appel à une API pour vérifier le Riot ID
    if (riotId.trim().includes("#")) {
      setLinked(true);
      // Ici tu pourrais stocker dans la DB ou appeler ton backend
      console.log("Compte Valorant lié :", riotId);
    } else {
      alert("Format Riot ID invalide. Ex : Charly#NA1");
    }
  };

  return (
    <div className="max-w-md mx-auto mt-12 bg-white p-6 shadow rounded">
      <h1 className="text-2xl font-bold text-blue-700 text-center mb-6">
        Lier mon compte Valorant
      </h1>

      {!linked ? (
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            placeholder="Riot ID (ex: Charly#NA1)"
            value={riotId}
            onChange={(e) => setRiotId(e.target.value)}
            className="w-full p-2 border rounded"
            required
          />
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white py-2 w-full rounded"
          >
            Lier mon compte
          </button>
        </form>
      ) : (
        <div className="text-center text-green-600 font-medium">
          ✅ Compte lié avec succès : <br />
          <span className="font-bold">{riotId}</span>
        </div>
      )}
    </div>
  );
}
