import { useState, useEffect } from "react";

export default function Competitions() {
  const [isRegistered, setIsRegistered] = useState(false);
  const [riotLinked, setRiotLinked] = useState(true);
  const [timeLeft, setTimeLeft] = useState("");
  const [started, setStarted] = useState(false);
  const [tournamentEnded, setTournamentEnded] = useState(false);

  const [players, setPlayers] = useState(() => {
    const saved = localStorage.getItem("players");
    return saved ? JSON.parse(saved) : [];
  });

  const [userPseudo, setUserPseudo] = useState("");

  const leaderboard = [
    { pseudo: "PlayerOne", score: 397 },
    { pseudo: "ValorantGod", score: 382 },
    { pseudo: "FlashAim", score: 376 },
    { pseudo: "NoScopeMaster", score: 362 },
    { pseudo: "TopFrag", score: 349 },
    { pseudo: "JettMain", score: 340 },
    { pseudo: "AceHunter", score: 332 },
    { pseudo: "OneTapKing", score: 321 },
    { pseudo: "SharpShooter", score: 319 },
    { pseudo: "ClutchLord", score: 310 },
  ];

  const getNextSunday18h = () => {
    const now = new Date();
    const result = new Date();
    result.setDate(now.getDate() + ((7 - now.getDay()) % 7));
    result.setHours(18, 0, 0, 0);
    return result;
  };

  const addToGlobalRanking = (leaderboard) => {
    const points = [100, 80, 60, 50, 40, 30, 20, 10, 5, 3];
    const stored = JSON.parse(localStorage.getItem("globalRankingData")) || {};

    leaderboard.forEach((player, index) => {
      const pseudo = player.pseudo;
      const score = points[index] || 1;

      if (!stored[pseudo]) {
        stored[pseudo] = { points: 0, participations: 0 };
      }

      stored[pseudo].points += score;
      stored[pseudo].participations += 1;
    });

    localStorage.setItem("globalRankingData", JSON.stringify(stored));
  };

  useEffect(() => {
    const target = getNextSunday18h();
    const now = new Date();
    const savedLastReset = localStorage.getItem("lastReset");
    const lastReset = savedLastReset ? new Date(savedLastReset) : null;

    if (!lastReset || lastReset < target && now > target) {
      localStorage.setItem("players", JSON.stringify([]));
      setPlayers([]);
      localStorage.setItem("lastReset", now.toISOString());
      localStorage.removeItem("rankingUpdated");
    }

    const updateCountdown = () => {
      const now = new Date();
      const diff = target - now;

      if (diff <= 0 && diff > -1000 * 60 * 60 * 24) {
        setStarted(true);
        setTimeLeft("üèÅ Tournoi en cours !");
      }

      if (diff <= -1000 * 60 * 60) {
        setTournamentEnded(true);
        setTimeLeft("‚úÖ Tournoi termin√©");

        if (!localStorage.getItem("rankingUpdated")) {
          addToGlobalRanking(leaderboard);
          localStorage.setItem("rankingUpdated", "true");
        }
      }

      if (diff > 0) {
        const hours = Math.floor(diff / 1000 / 60 / 60);
        const minutes = Math.floor((diff / 1000 / 60) % 60);
        const seconds = Math.floor((diff / 1000) % 60);
        setTimeLeft(`${hours}h ${minutes}m ${seconds}s`);
      }
    };

    updateCountdown();
    const interval = setInterval(updateCountdown, 1000);
    return () => clearInterval(interval);
  }, []);

  const handleRegister = () => {
    if (!riotLinked) {
      alert("‚ùå Tu dois lier ton compte Valorant pour participer.");
      return;
    }

    if (!userPseudo.trim()) {
      alert("‚ö†Ô∏è Entrez votre pseudo avant de vous inscrire.");
      return;
    }

    if (players.includes(userPseudo)) {
      alert("‚ùå Tu es d√©j√† inscrit √† ce tournoi.");
      return;
    }

    const updated = [...players, userPseudo];
    setPlayers(updated);
    localStorage.setItem("players", JSON.stringify(updated));
    setIsRegistered(true);
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded shadow">
      <h1 className="text-3xl font-bold text-center mb-4 text-blue-700">Tournoi Solo Hebdomadaire</h1>
      <p className="text-center text-lg text-gray-800 mb-4">
        ‚è≥ {timeLeft}
      </p>
      <p className="text-center text-gray-600 mb-6">
        üìÜ Tous les dimanches √† 18h ‚Äì üí∏ Gratuit ‚Äì üß© Combat Score uniquement ‚Äì üèÜ Top 3 = troph√©es
      </p>

      {!isRegistered ? (
        <>
          <input
            type="text"
            placeholder="Ton pseudo"
            value={userPseudo}
            onChange={(e) => setUserPseudo(e.target.value)}
            className="w-full p-2 border rounded mb-4"
          />
          <button
            onClick={handleRegister}
            className="block mx-auto bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded font-semibold"
          >
            S'inscrire
          </button>
        </>
      ) : (
        <p className="text-center text-green-600 font-medium">
          ‚úÖ Tu es inscrit √† ce tournoi.
        </p>
      )}

      <hr className="my-8" />

      <h2 className="text-xl font-bold mb-4 text-center">üèÜ Leaderboard ‚Äì Top 10</h2>
      <table className="w-full border text-left mb-8">
        <thead className="bg-gray-100">
          <tr>
            <th className="p-2 border">#</th>
            <th className="p-2 border">Pseudo</th>
            <th className="p-2 border">Combat Score</th>
          </tr>
        </thead>
        <tbody>
          {leaderboard.map((player, index) => (
            <tr key={index} className="hover:bg-gray-50">
              <td className="p-2 border font-bold">{index + 1}</td>
              <td className="p-2 border">{player.pseudo}</td>
              <td className="p-2 border">{player.score}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {tournamentEnded && (
        <div className="mt-8 text-center text-xl text-green-600 font-semibold">
          üèÅ Le tournoi est termin√© ! Voici les r√©sultats finaux :
        </div>
      )}

      <h2 className="text-xl font-bold mb-2 text-center mt-8">üë• Participants inscrits</h2>
      <ul className="list-disc ml-6 text-gray-700">
        {players.map((p, i) => (
          <li key={i}>{p}</li>
        ))}
      </ul>
    </div>
  );
}
