import { useState } from "react";

export default function Competitions() {
  const [isRegistered, setIsRegistered] = useState(false);
  const [riotLinked, setRiotLinked] = useState(true); // mettre sur false pour tester

  const leaderboard = [
    { pseudo: "PlayerOne", score: 397 },
    { pseudo: "ValorantGod", score: 382 },
    { pseudo: "FlashAim", score: 376 },
    { pseudo: "NoScopeMaster", score: 362 },
    { pseudo: "TopFrag", score: 349 },
    { pseudo: "JettMain", score: 340 },
    { pseudo: "AceHunter", score: 332 },
    { pseudo: "OneTapKing", score: 321 },
    { pseudo: "SharpShooter", score: 319 },
    { pseudo: "ClutchLord", score: 310 },
  ];

  const handleRegister = () => {
    if (!riotLinked) {
      alert("‚ùå Tu dois lier ton compte Valorant pour participer.");
      return;
    }
    setIsRegistered(true);
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-white rounded shadow">
      <h1 className="text-3xl font-bold text-center mb-6 text-blue-700">Tournoi Solo Hebdomadaire</h1>
      <p className="text-center text-gray-600 mb-6">
        üìÜ D√©but : Dimanche prochain ‚Äì üí∏ Gratuit ‚Äì üß© Combat Score uniquement ‚Äì üèÜ Top 3 = troph√©es
      </p>

      {!isRegistered ? (
        <button
          onClick={handleRegister}
          className="block mx-auto bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded font-semibold"
        >
          S'inscrire
        </button>
      ) : (
        <p className="text-center text-green-600 font-medium">
          ‚úÖ Tu es inscrit √† ce tournoi.
        </p>
      )}

      <hr className="my-8" />

      <h2 className="text-xl font-bold mb-4 text-center">üèÜ Leaderboard ‚Äì Top 10</h2>
      <table className="w-full border text-left">
        <thead className="bg-gray-100">
          <tr>
            <th className="p-2 border">#</th>
            <th className="p-2 border">Pseudo</th>
            <th className="p-2 border">Combat Score</th>
          </tr>
        </thead>
        <tbody>
          {leaderboard.map((player, index) => (
            <tr key={index} className="hover:bg-gray-50">
              <td className="p-2 border font-bold">{index + 1}</td>
              <td className="p-2 border">{player.pseudo}</td>
              <td className="p-2 border">{player.score}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
