import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useState } from "react";

// Fake Auth
import { AuthProvider, useAuth } from "./auth/FakeAuthContext";

// Layout
import Sidebar from "./components/Sidebar";
import Home from "./pages/Home";
import Competitions from "./pages/Competitions";
import Account from "./pages/Account";
import LinkValorant from "./pages/LinkValorant";
import Support from "./pages/Support";
import Login from "./pages/Login";

function Header({ toggleSidebar }) {
  const { user, logout } = useAuth();

  return (
    <header className="bg-red-600 text-white px-6 py-3 flex justify-between items-center">
      <h1 className="text-xl font-bold">CDesport</h1>
      <div className="flex items-center space-x-4">
        {user ? (
          <>
            <span className="text-sm">ðŸ‘¤ {user.email}</span>
            <button
              onClick={logout}
              className="bg-white text-red-600 px-3 py-1 rounded font-semibold"
            >
              DÃ©connexion
            </button>
          </>
        ) : (
          <Link to="/login" className="bg-white text-red-600 px-3 py-1 rounded font-semibold">
            Connexion
          </Link>
        )}
        <button
          onClick={toggleSidebar}
          className="bg-white text-red-600 px-3 py-1 rounded font-semibold"
        >
          â˜°
        </button>
      </div>
    </header>
  );
}

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  return (
    <AuthProvider>
      <Router>
        <div className="min-h-screen bg-gray-100">
          <Header toggleSidebar={toggleSidebar} />
          <Sidebar isOpen={sidebarOpen} toggleSidebar={toggleSidebar} />
          <main className="p-4">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/competitions" element={<Competitions />} />
              <Route path="/account" element={<Account />} />
              <Route path="/link-valorant" element={<LinkValorant />} />
              <Route path="/support" element={<Support />} />
              <Route path="/login" element={<Login />} />
            </Routes>
          </main>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
