export default function Sidebar({ isOpen, toggleSidebar }) {
  return (
    <div
      className={`fixed top-0 left-0 h-full w-64 bg-blue-700 text-white transform ${
        isOpen ? "translate-x-0" : "-translate-x-full"
      } transition-transform duration-300 z-50`}
    >
      <div className="p-4 flex justify-between items-center">
        <h2 className="text-lg font-bold">Menu</h2>
        <button onClick={toggleSidebar}>❌</button>
      </div>
      <ul className="p-4 space-y-3">
        <li><a href="/" className="hover:underline">Accueil</a></li>
        <li><a href="/competitions" className="hover:underline">Compétitions</a></li>
        <li><a href="/account" className="hover:underline">Mon Compte</a></li>
        <li><a href="/link-valorant" className="hover:underline">Lier Valorant</a></li>
        <li><a href="/support" className="hover:underline">Support</a></li>
      </ul>
    </div>
  );
}
