import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { useState } from "react";

// Auth
import { AuthProvider } from "./auth/AuthContext";

import RequireAuth from "./auth/RequireAuth";

// Layout
import Sidebar from "./components/Sidebar";

// Pages
import Home from "./pages/Home";
import Competitions from "./pages/Competitions";
import Account from "./pages/Account";
import LinkValorant from "./pages/LinkValorant";
import Support from "./pages/Support";
import Login from "./auth/Login";
import Register from "./auth/Register";

// Header (intégré ici)
function Header({ toggleSidebar }) {
  const { user, logout } = useAuth();

  return (
    <header className="bg-red-600 text-white px-6 py-3 flex justify-between items-center">
      <h1 className="text-xl font-bold">CDesport</h1>
      <div className="flex items-center space-x-4">
        {user ? (
          <>
            <span className="text-sm">👤 {user.email}</span>
            <span className="text-sm">💰 {user.balance}$</span>
            <button
              onClick={logout}
              className="bg-white text-red-600 px-3 py-1 rounded font-semibold"
            >
              Déconnexion
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="bg-white text-red-600 px-3 py-1 rounded font-semibold">
              Connexion
            </Link>
            <Link to="/register" className="bg-white text-red-600 px-3 py-1 rounded font-semibold">
              S’inscrire
            </Link>
          </>
        )}
        <button
          onClick={toggleSidebar}
          className="bg-white text-red-600 px-3 py-1 rounded font-semibold"
        >
          ☰
        </button>
      </div>
    </header>
  );
}

function App() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  return (
    <AuthProvider>
      <Router>
        <div className="min-h-screen bg-gray-100">
          <Header toggleSidebar={toggleSidebar} />
          <Sidebar isOpen={sidebarOpen} toggleSidebar={toggleSidebar} />
          <main className="p-4">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/competitions" element={<Competitions />} />
              <Route
                path="/account"
                element={
                  <RequireAuth>
                    <Account />
                  </RequireAuth>
                }
              />
              <Route
                path="/link-valorant"
                element={
                  <RequireAuth>
                    <LinkValorant />
                  </RequireAuth>
                }
              />
              <Route path="/support" element={<Support />} />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
            </Routes>
          </main>
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
