import { Link } from "react-router-dom";

export default function Sidebar({ isOpen, toggleSidebar }) {
  return (
    <div
      className={`fixed top-0 left-0 h-full w-64 bg-blue-700 text-white transform transition-transform duration-300 z-50 ${
        isOpen ? "translate-x-0" : "-translate-x-full"
      }`}
    >
      <div className="flex justify-between items-center p-4">
        <h2 className="text-lg font-bold">Menu</h2>
        <button onClick={toggleSidebar}>❌</button>
      </div>
      <ul className="p-4 space-y-4">
        <li><Link to="/" onClick={toggleSidebar}>Accueil</Link></li>
        <li><Link to="/account" onClick={toggleSidebar}>Mon compte</Link></li>
        <li><Link to="/competitions" onClick={toggleSidebar}>Compétitions</Link></li>
        <li><Link to="/link-valorant" onClick={toggleSidebar}>Lier compte Valorant</Link></li>
        <li><Link to="/support" onClick={toggleSidebar}>Support</Link></li>
      </ul>
    </div>
  );
}
