import { useEffect, useState } from "react";

export default function Ranking() {
  const [globalRanking, setGlobalRanking] = useState([]);

  useEffect(() => {
    const now = new Date();
    const lastReset = localStorage.getItem("rankingResetDate");

    // Réinitialisation automatique chaque 1er du mois
    if (now.getDate() === 1 && lastReset !== now.toDateString()) {
      localStorage.removeItem("globalRankingData");
      localStorage.setItem("rankingResetDate", now.toDateString());
      console.log("Classement global réinitialisé automatiquement.");
    }

    const stored = JSON.parse(localStorage.getItem("globalRankingData")) || {};

    const formatted = Object.entries(stored).map(([pseudo, data]) => ({
      pseudo,
      participations: data.participations,
      totalPoints: data.points,
    }));

    formatted.sort((a, b) => b.totalPoints - a.totalPoints);
    setGlobalRanking(formatted);
  }, []);

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded shadow">
      <h1 className="text-3xl font-bold text-center text-blue-700 mb-6">
        Classement Global – Mois en cours
      </h1>

      {globalRanking.length > 0 ? (
        <table className="w-full border text-left">
          <thead className="bg-gray-100">
            <tr>
              <th className="p-2 border">#</th>
              <th className="p-2 border">Pseudo</th>
              <th className="p-2 border">Participations</th>
              <th className="p-2 border">Points</th>
            </tr>
          </thead>
          <tbody>
            {globalRanking.map((player, index) => (
              <tr key={index} className="hover:bg-gray-50">
                <td className="p-2 border font-bold">{index + 1}</td>
                <td className="p-2 border">{player.pseudo}</td>
                <td className="p-2 border">{player.participations}</td>
                <td className="p-2 border">{player.totalPoints}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-center text-gray-600">Aucune donnée de classement encore disponible.</p>
      )}
    </div>
  );
}
