import { useState } from "react";

const agentImages = [
  "jett.png",
  "raze.png",
  "reyna.png",
  "fade.png",
  // ajoute ici les autres noms
];

export default function Account() {
  const [selectedAvatar, setSelectedAvatar] = useState("jett.png"); // avatar par défaut

  const handleAvatarSelect = (avatar) => {
    setSelectedAvatar(avatar);
    // ici tu pourrais aussi sauvegarder ça dans la base de données ou localStorage
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Mon compte</h1>

      <div className="mb-4">
        <h2 className="font-semibold mb-2">Choisis ton agent</h2>
        <div className="grid grid-cols-4 gap-4">
          {agentImages.map((img) => (
            <img
              key={img}
              src={`/agents/${img}`}
              alt={img}
              className={`cursor-pointer border-2 rounded ${
                selectedAvatar === img ? "border-blue-500" : "border-transparent"
              }`}
              onClick={() => handleAvatarSelect(img)}
            />
          ))}
        </div>
      </div>

      <div className="mt-6">
        <p className="font-semibold">Aperçu :</p>
        <img
          src={`/agents/${selectedAvatar}`}
          alt="avatar"
          className="w-24 h-24 rounded-full mt-2 border"
        />
      </div>
    </div>
  );
}
